#!/bin/bash

SITE_PACKAGES_PATH=/mnt/site-packages
export PYTHONPATH="${SITE_PACKAGES_PATH}${PYTHONPATH:+:${PYTHONPATH}}"
export GIT_PYTHON_GIT_EXECUTABLE=/run/dojo/bin/git

# For users to have access to the same PYTHONPATH
tee -a /etc/bash.bashrc << 'EOF'
SITE_PACKAGES_PATH=/mnt/site-packages
export PYTHONPATH="${SITE_PACKAGES_PATH}${PYTHONPATH:+:}${PYTHONPATH}"
export GIT_PYTHON_GIT_EXECUTABLE=/run/dojo/bin/git
EOF